async function n(){console.log("Fetching traffic data...");try{const t="/sushant-portfolio/traffic.json?t="+Date.now();console.log("Fetching from URL:",t);const e=await fetch(t);if(console.log("Response status:",e.status),e.ok){const o=await e.json();console.log("Traffic data received:",o);const c=o.last_24h?.views||o.total_views||0;console.log("Views to display:",c),document.getElementById("traffic-count").textContent=c,console.log("Traffic count updated successfully")}else console.error("Failed to fetch traffic.json:",e.status),document.getElementById("traffic-count").textContent="N/A"}catch(t){console.error("Failed to fetch traffic data:",t),document.getElementById("traffic-count").textContent="Error"}}document.addEventListener("DOMContentLoaded",n);document.querySelectorAll('a[href^="#"]').forEach(t=>{t.addEventListener("click",function(e){e.preventDefault();const o=document.querySelector(this.getAttribute("href"));o&&o.scrollIntoView({behavior:"smooth",block:"start"})})});
